import React, { Component } from "react";
import TeamMember from "./TeamMember";
import memberInfo from "./memberInfo"
import { Link, useParams } from 'react-router-dom';

function MemberDetails () {
    const { id } = useParams();
    const idd=parseInt(id, 10);
    return(
        <div className="col-md-4 col-sm-6">
            <div className="card">
                <div className="card-header">
                    <img style={{maxWidth: '100%'}} src={memberInfo[idd-1].img}/>
                </div>
                <div className="card-body">
                    <h2>{memberInfo[idd-1].name}</h2>
                    <h5>{memberInfo[idd-1].position}</h5>
                    <div>{memberInfo[idd-1].phone}</div>
                    <div>{memberInfo[idd-1].email}</div>
                    <div>{memberInfo[idd-1].website}</div>
                </div>
            </div>
        </div>
    )
}


export default MemberDetails;
