import React, { Component } from "react";
import memberInfo from "./memberInfo"
import { useParams } from 'react-router-dom';

function MemberDetails ({match}) {
    const { id } = useParams();
    console.log(id);
    const member = memberInfo[id-1];
    return(
        
            <div style={{display: "flex",flexDirection: "row",justifyContent: "center",alignItems: "center",margin: "0.5rem"}} >
                <div style={{borderWidth: '400px'}}>
                    <img style={{maxWidth: '40%'}} src={member.img}/>
                </div>
                <div style={{maxWidth: '40%'}} >
                    <h1>{member.name}</h1>
                    <h3>{member.position}</h3>
                    <div>{member.phone}</div>
                    <div>{member.email}</div>
                    <div>{member.website}</div>
                </div>
            </div>
        
    )
}

export default MemberDetails;

