import React, { Component } from "react";
import { DEFAULT_BREAKPOINTS } from "react-bootstrap/esm/ThemeProvider";
import { Link } from "react-router-dom"

class TeamMember extends Component {
    render(){
    return(
        <Link to={`/member/${this.props.key}`}className="col-md-4 col-sm-6" style={{ color: 'inherit', textDecoration: 'inherit' }}>
            <div className="card">
                <div className="card-header">
                    <img style={{maxWidth: '100%'}} src={this.props.img}/>
                </div>
                <div className="card-body" 
                     style={{backgroundColor : this.props.website ? "white" : "black"}}>
                    <h2>{this.props.name}</h2>
                    <h5>{this.props.position}</h5>
                    <div>{this.props.phone}</div>
                    <div>{this.props.email}</div>
                    <div>{this.props.website}</div>
                </div>
            </div>
        </Link>
    )
}
}

export default TeamMember;
